  8→import type { ISiteStat } from '@/db/types';
  9→import { useLiveQuery } from '@/composables/useDexieLiveQuery';
 10→import DateNavigator from '@/components/DateNavigator.vue';
 11→import TrendChart, { type ChartItem } from '@/components/TrendChart.vue';
 12→
 13→const contentVariants = {
 14→  hidden: { opacity: 0 },
 15→  show: {
