 33→
 34→const listItemVariants = {
 35→  hidden: { opacity: 0, x: -12 },
 36→  show: { opacity: 1, x: 0, transition: { duration: 0.25 } },
 37→  exit: { opacity: 0, x: 12, transition: { duration: 0.15 } },
 38→};
 39→
 40→const router = useRouter();
 41→const { view, date, startDate, endDate, label, next, prev, goToday, isToday, canNext } = useDateRange();
 42→
 43→type TrendData =
 44→  | { hour: string; duration: number }
 45→  | { date: string; duration: number };
 46→
 47→interface HomeData {
 48→  sites: ISiteStat[];
 49→  trend: TrendData[];
 50→}
 51→
 52→const homeData = useLiveQuery<HomeData>(
 53→  async () => {
 54→    if (view.value === 'day') {
 55→      const [sitesData, trend] = await Promise.all([
 56→        getAggregatedSites(startDate.value, endDate.value, 20),
 57→        getHourlyTrend(startDate.value),
 58→      ]);
 59→      return { sites: sitesData, trend };
 60→    }
 61→    // Optimization: Fetch sites and trend in one DB pass for range views
 62→    const result = await getRangeStats(startDate.value, endDate.value, 20);
 63→    return { sites: result.sites, trend: result.trend };
 64→  },
 65→  { sites: [], trend: [] },
 66→  [view, startDate, endDate],
 67→);
 68→
 69→const sites = computed(() => homeData.value.sites);
 70→const trendData = computed(() => homeData.value.trend);
 71→
 72→const mapHourlyToChartItem = (item: { hour: string; duration: number }): ChartItem => {
 73→  const h = parseInt(item.hour, 10);
 74→  const showLabel = h % 4 === 0;
 75→  return {
 76→    key: `h-${item.hour}`,
 77→    value: item.duration,
 78→    label: showLabel ? `${h}:00` : '',
 79→    tooltip: `${h}:00, ${prettyMs(item.duration, { compact: true })}`,
 80→    ariaLabel: `${h}:00, ${prettyMs(item.duration, { verbose: true })}`,
 81→    active: false
 82→  };
 83→};
 84→
 85→const mapDailyToChartItem = (item: { date: string; duration: number }): ChartItem => {
 86→  const d = new Date(item.date + 'T00:00:00');
 87→  let label = '';
 88→  if (trendData.value.length > 10) {
 89→     label = d.getDate().toString();
 90→  } else {
 91→     label = d.toLocaleDateString(undefined, { weekday: 'narrow' });
 92→  }
 93→
 94→  return {
 95→    key: item.date,
 96→    value: item.duration,
 97→    label,
 98→    tooltip: `${d.toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}: ${prettyMs(item.duration, { compact: true })}`,
 99→    ariaLabel: `${d.toLocaleDateString(undefined, { month: 'long', day: 'numeric' })}: ${prettyMs(item.duration, { verbose: true })}`,
100→    active: item.date === date.value
101→  };
102→};
103→
104→const chartItems = computed<ChartItem[]>(() => {
105→  return trendData.value.map(item =>
106→    'hour' in item ? mapHourlyToChartItem(item) : mapDailyToChartItem(item)
107→  );
108→});
109→
110→const chartRenderKey = computed(() => `${view.value}-${startDate.value}-${endDate.value}`);
111→
112→const totalDuration = computed(() => {
113→  return sites.value.reduce((sum, site) => sum + site.duration, 0);
114→});
115→
116→const sitePercentage = (duration: number): number => {
117→  if (totalDuration.value === 0) return 0;
118→  return Math.round((duration / totalDuration.value) * 100);
119→};
120→
121→const getSiteLabel = (site: ISiteStat, index: number): string => {
122→  const duration = prettyMs(site.duration, { secondsDecimalDigits: 0, verbose: true });
123→  return `Rank ${index + 1}, ${site.hostname}, time spent ${duration}`;
124→};
125→
126→const goToDetail = (hostname: string) => {
127→  router.push({
128→    path: `/site/${hostname}`,
129→    query: { view: view.value, date: date.value }
130→  });
131→};
132→
133→const goToHistory = () => {
134→  router.push({
135→    path: '/history',
136→    query: { view: view.value, date: date.value }
137→  });
138→};
139→
140→const updateView = (v: ViewMode) => {
141→  view.value = v;
142→};
143→</script>
