194→        <div class="card-body p-4 items-center text-center">
195→          <div class="text-base-content/60 text-xs font-bold uppercase tracking-widest">Total Active Time</div>
196→          <div class="text-3xl font-black text-primary font-mono">
197→            {{ totalDuration > 0 ? prettyMs(totalDuration, {secondsDecimalDigits: 0}) : '0s' }}
198→          </div>
199→        </div>
200→      </motion.div>
201→
202→      <!-- Trend Chart -->
