204→        <div class="card-body p-2">
205→           <TrendChart :key="chartRenderKey" :items="chartItems" />
206→        </div>
207→      </motion.div>
208→
209→      <!-- Sites List -->
210→      <motion.div :variants="cardVariant" class="flex flex-col gap-2">
211→        <div class="text-xs font-bold text-base-content/40 uppercase px-2">Top Sites</div>
212→
213→        <AnimatePresence mode="wait">
214→          <motion.div
215→            v-if="sites.length === 0"
216→            key="empty"
217→            :initial="{ opacity: 0 }"
218→            :animate="{ opacity: 0.6 }"
219→            :exit="{ opacity: 0 }"
