221→          >
222→            <svg class="size-12 text-base-content/30" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="1.5" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
223→            <div class="text-sm font-medium">No activity recorded</div>
224→            <div class="text-xs">Browse some sites to see data here.</div>
225→          </motion.div>
226→
227→          <motion.div
228→            v-else
229→            key="list"
230→            class="flex flex-col gap-1"
231→            :variants="listContainerVariants"
232→            initial="hidden"
233→            animate="show"
234→          >
235→            <motion.button
236→              v-for="(site, index) in sites"
237→              :key="site.hostname"
238→              layout
